import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';


export default function UpdateContact() {
   // const [inputs, setInputs] = useState([])
   const [values, setValues] = useState({name:"", contact:""})
    const { id } = useParams();
    useEffect(() => {
        axios.get('http://localhost:3000/contacts/' + id)
            .then(res => {
                //setInputs(res.data)
                setValues(res.data);
            }
            )
            .catch(err => console.log(err));
    })

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setValues(values => ({ ...values, [name]: value }))
    }
    const navigate = useNavigate();
    const handleSubmit = (event) => {
        event.preventDefault();
        axios.put("http://localhost:3000/contacts/"+id, inputs)
            .then(res => {
                console.log(res);
                navigate("/contact");
            })
            .catch(err => console.log(err));
        clearState();
    }
    const clearState = () => {
        setInputs('');
    };




    return (
        <div>
            <div className='container w-60'>
                <h1>Update Contact</h1>
                <div className='form-group row'>
                    <div className='col-6'>
                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Name:</label>
                                <input type="text" name='name' id="name" className="form-control" value={values.name} onChange={handleChange} />

                            </div>
                            <div className="form-group">
                                <label htmlFor="exampleInputEmail1">Contact: </label>
                                <input type="text" name='contact' id='contact' className="form-control" value={values.contact} onChange={handleChange} />

                            </div>
                            <div className="form-group">

                                <button type='submit' name='Contact' className="btn btn-primary">Update</button>
                                <Link to="/" name='Contact' className="btn btn-primary">Back</Link>

                            </div>

                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
}
