import React, {useState} from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';


export default function UpdateContact() {
    const [inputs, setInputs] = useState([])
    const { id } = useParams;

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }
    const navigate = useNavigate();
    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
        axios.post("http://localhost:3000/contacts",inputs)
        .then(res => {
            console.log(res);
            navigate("/contact");
        })
        .catch(err => console.log(err));
        clearState();
    }
    const clearState = () => {
        setInputs('');
    };




  return (
    <div>
       <div className='container w-60'>
        <h1>Update Contact</h1>
            <div className='form-group row'>
                <div className='col-6'>
                    <form onSubmit={handleSubmit}>
                        <div class="form-group">
                            <label for="exampleInputEmail1">Name:</label>
                            <input type="text" name='name' id="name" className="form-control" value={inputs.name || ""} onChange={handleChange} />

                        </div>
                        <div class="form-group">
                            <label for="exampleInputEmail1">Contact: </label>
                            <input type="text" name='contact' id='contact' className="form-control" value={inputs.contact || ""} onChange={handleChange} />

                        </div>
                        <div class="form-group">

                                <button type='submit' name='Contact' class="btn btn-primary">Update</button>
                                <Link to="/" name='Contact' class="btn btn-primary">Back</Link>

                        </div>

                    </form>
                </div>
            </div>
        </div>
    </div>
  )
}
