import React, { useState } from 'react'

export default function AddContact() {
    const [inputs, setInputs] = useState("")

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
        /* const val = {
             inputs.name,
             city,
         };*/
        //props.func(inputs);
        clearState();
    }
    const clearState = () => {
        setInputs('');
    };

    return (
        <div className='container'>
            <form onSubmit={handleSubmit}>
                <div class="form-group">
                    <label for="exampleInputEmail1">Name:</label>
                    <input type="text" name='name' id="name" className="form-control" value={inputs.name || ""} onChange={handleChange} />

                </div>
                <div class="form-group">
                    <label for="exampleInputEmail1">Contact: </label>
                    <input type="text" name='contact' id='contact' className="form-control" value={inputs.contact || ""} onChange={handleChange} />

                </div>
                <div class="form-group">
                    <label>
                        <button type='submit' name='Contact' class="btn btn-primary">Enter</button>
                    </label>
                </div>

            </form>
        </div>
    )
}
